const INITIAL_STATE = {
  name: '',
}

export default function formReducer(state = INITIAL_STATE, action) {
  switch (action.type) {
    case SELECT_MOVIE:
      return {
        ...state,
        name: action.payload.inputValue,
      };
    default:
      return state;
  }
}
