import React, { Component } from 'react';
import { connect } from 'react-redux';
import formAction from '../actions/FormActions'

class Form extends Component {
  renderForms = (type, value, name, setName ) => (
    <input
    type={ type }
    value={ value }
    name={ name }
    onChange={ () => setName({ target: { name, value, type, checked } }) }
    />
  )

  render() {
    const { name, setName } = this.props;
    return (
      <div className="Form">
        <form>
          { this.renderForms('text', name, 'nome', setName) }
        </form>
      </div>
    );
  }
}

const mapStateToProps = (state) => ({
  name: state.formReducer.name
});

const mapDispatchToProps = (dispatch) => ({
  setName: (name) => dispatch(
    formAction(name),
  ),
});

export default connect (mapStateToProps, mapDispatchToProps)(Form);
