import SELECT_MOVIE from '../actions/index';

const INITIAL_STATE = {
  name: '',
}

export default function formReducer(state = INITIAL_STATE, action) {
  switch (action.type) {
    case SELECT_MOVIE:
      return {
        name: action.payload,
      };
    default:
      return state;
  }
}
