import React, { Component } from 'react'

class Form extends Component {
  renderForms = (type, value, name, onChange ) => (
    <input
    type={ type }
    value={ value }
    name
    />
  )

  render() {
    return (
      <div className="Form">
        <form>
          { this.renderForms('Text', state.name) }
        </form>
      </div>
    );
  }
}

export default Form;
